import { FunnelPlotOutlined, SearchOutlined } from '@ant-design/icons';
import { Button, Input, InputNumber, Space, Table, InputRef, Popconfirm, Form, Typography} from 'antd';
import Item from 'antd/es/descriptions/Item';
import type { ColumnsType, ColumnType} from 'antd/es/table';
import type { FilterConfirmProps } from 'antd/es/table/interface';
import React, { useRef, useState } from 'react';
import Highlighter from 'react-highlight-words';

interface Item {
    key: string;
    name: string;
    email: string;
    gender: string;
    birthDate: string;
    phone: string;
};

interface EditableCellProps extends React.HTMLAttributes<HTMLElement> {
    editing: boolean;
    dataIndex: string;
    title: any;
    inputType: 'number' | 'text';
    record: Item;
    index: number;
    children: React.ReactNode;
  }
  
  const EditableCell: React.FC<EditableCellProps> = ({
    editing,
    dataIndex,
    title,
    inputType,
    record,
    index,
    children,
    ...restProps
  }) => {
    const inputNode = inputType === 'number' ? <InputNumber /> : <Input />;
  
    return (
      <td {...restProps}>
        {editing ? (
          <Form.Item
            name={dataIndex}
            style={{ margin: 0 }}
            rules={[
              {
                required: true,
                message: `Please Input ${title}!`,
              },
            ]}
          >
            {inputNode}
          </Form.Item>
        ) : (
          children
        )}
      </td>
    );
  };

type DataIndex = keyof Item;

const App: React.FC = () => {    
    const [dataSource, setDataSource] = useState<Item[]>([
        {
            key: "1",
            name: 'Matheus',
            email: 'matheus_orbotafogo@hotmail.com',
            gender: 'Masculino',
            birthDate: "10/02/1999",
            phone: "(43) 9999-9999"
        },
        {
            key: "2",
            name: 'serg',
            email: 'teste@gmail.com',
            gender: 'Masculino',
            birthDate: "10/02/1999",
            phone: "(43) 9999-9999"
        },
        {
            key: "3",
            name: 'rar',
            email: 'ola@outlook.com',
            gender: 'Masculino',
            birthDate: "10/02/1999",
            phone: "(43) 9999-9999"
        },
        {
            key: "4",
            name: 'Matheaeaeeus',
            email: 'matheus@hotmail.com',
            gender: 'Masculino',
            birthDate: "10/02/1999",
            phone: "(43) 9999-9999"
        },
    ]);

    const [searchText, setSearchText] = useState('');
    const [searchedColumn, setSearchedColumn] = useState('');
    const searchInput = useRef<InputRef>(null);
    const [data, setData] = useState(dataSource);
    const [editingKey, setEditingKey] = useState('');
    const [form] = Form.useForm();

    const isEditing = (record: Item) => record.key === editingKey;

    
    const handleSearch = (
        selectedKeys: string[],
        confirm: (param?: FilterConfirmProps) => void,
        dataIndex: DataIndex,
    ) => {
        confirm();
        setSearchText(selectedKeys[0]);
        setSearchedColumn(dataIndex);
    };

    const handleDelete = (key: React.Key) => {
        const newData = dataSource.filter((item) => item.key !== key);
        setDataSource(newData);
    };

    
    const handleReset = (clearFilters: () => void) => {
        clearFilters();
        setSearchText('');
    };

    const getColumnSearchProps = (dataIndex: DataIndex): ColumnType<Item> => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters, close }) => (
          <div style={{ padding: 8 }} onKeyDown={e => e.stopPropagation()}>
            <Input
              ref={searchInput}
              placeholder={`Buscar ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => handleSearch(selectedKeys as string[], confirm, dataIndex)}
              style={{ marginBottom: 8, display: 'block' }}
            />
            <Space>
              <Button
                type="primary"
                onClick={() => handleSearch(selectedKeys as string[], confirm, dataIndex)}
                icon={<SearchOutlined />}
                size="small"
                style={{ width: 90 }}
              >
                Buscar
              </Button>
              <Button
                type="primary"
                size="small"
                icon={<FunnelPlotOutlined />}
                onClick={() => {
                  confirm({ closeDropdown: false });
                  setSearchText((selectedKeys as string[])[0]);
                  setSearchedColumn(dataIndex);
                }}
              >
                Filtrar
              </Button>
    
              <Button
                onClick={() => clearFilters && handleReset(clearFilters)}
                size="small"
                style={{ width: 90 }}
              >
                Resetar
              </Button>
    
              <Button
                type="link"
                size="small"
                onClick={() => {
                  close();
                }}
              >
                Fechar
              </Button>
            </Space>
          </div>
        ),
        filterIcon: (filtered: boolean) => (
          <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes((value as string).toLowerCase()),
        onFilterDropdownOpenChange: visible => {
          if (visible) {
            setTimeout(() => searchInput.current?.select(), 100);
          }
        },
        render: text =>
          searchedColumn === dataIndex ? (
            <Highlighter
              highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
              searchWords={[searchText]}
              autoEscape
              textToHighlight={text ? text.toString() : ''}
            />
          ) : (
            text
        ),
    });  

    const edit = (record: Partial<Item> & { key: React.Key }) => {
        form.setFieldsValue({ name: '', email: '', gender: '', birthDate: '', phone: '', ...record });
        setEditingKey(record.key);
    };

  const cancel = () => {
    setEditingKey('');
  };

  const save = async (key: React.Key) => {
    try {
      const row = (await form.validateFields()) as Item;

      const newData = [...data];
      const index = newData.findIndex((item) => key === item.key);
      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, {
          ...item,
          ...row,
        });
        setData(newData);
        setEditingKey('');
      } else {
        newData.push(row);
        setData(newData);
        setEditingKey('');
      }
    } catch (errInfo) {
      console.log('Validate Failed:', errInfo);
    }
  };

    
    const columns = [
        {
            title: 'Nome',
            dataIndex: 'name',
            key: 'name',
            editable: true,
            ...getColumnSearchProps('name'),
        },
        {
            title: 'Email',
            dataIndex: 'email',
            key: 'email',
            editable: true,
            ...getColumnSearchProps('email'),
        },
        {
            title: 'Genêro',
            dataIndex: 'gender',
            editable: true,
        },
        {
            title: 'Data de Nascimento',
            dataIndex: 'birthDate',
            editable: true,
        },
        {
            title: 'Telefone',
            dataIndex: 'phone',
            editable: true,
        },
        {
            title: 'Remover',
            dataIndex:'',
            key:'x',
            render: (_: any, record: { key: React.Key }) =>
            dataSource.length >= 1 ? (
              <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record.key)}>
                <a>Delete</a>
              </Popconfirm>
            ) : null,
        },
        {
            title: 'Editar',
            dataIndex: 'edit',
            render: (_: any, record: Item) => {
                const editable = isEditing(record);
                return editable ? (
                  <span>
                    <Typography.Link onClick={() => save(record.key)} style={{ marginRight: 8 }}>
                      Save
                    </Typography.Link>
                    <Popconfirm title="Sure to cancel?" onConfirm={cancel}>
                      <a>Cancel</a>
                    </Popconfirm>
                  </span>
                ) : (
                  <Typography.Link disabled={editingKey !== ''} onClick={() => edit(record)}>
                    Edit
                  </Typography.Link>
                );
              },
        },     
    ] ;         
    
    const mergedColumns = columns.map((col) => {
        if (!col.editable) {
          return col;
        }
        return {
          ...col,
          onCell: (record: Item) => ({
            record,
            inputType: col.dataIndex === 'age' ? 'number' : 'text',
            dataIndex: col.dataIndex,
            title: col.title,
            editing: isEditing(record),
          }),
        };
      });

    return (
        <Form form={form} component={false}>
          <Table
            components={{
              body: {
                cell: EditableCell,
              },
            }}
            bordered
            dataSource={data}
            columns={mergedColumns}
            rowClassName="editable-row"
            pagination={{
              onChange: cancel,
            }}
          />
        </Form>
      );
};
export default App
